var app=angular.module("myApp",["ng"]);app.factory("$heartBeat",["$interval",function(a){return{startBeat:function(){promise=a(function(){console.log("in heart beat...")},500)},stopBeat:function(){a.cancel(promise)}}}]);app.controller("myCtrl",["$scope","$injector",function(a,b){customService=b.get("$heartBeat");a.start=function(){customService.startBeat()};a.stop=function(){customService.stopBeat()}}]);